mapper.select:
select a.id, a.code, a.createDate, a.name, a.state, a.ord, a.memo, a.createTime, a.modifyTime, a.createUserId, a.modifyUserId, a.headFile, a.type, a.level, a.company, a.companyType, a.industry, a.country, a.province, a.city, a.region, a.address, a.scale, a.tel, a.phone, a.email, a.idCard, a.idCardFile, a.fax, a.credit, a.licenseFile, a.position, a.technicalTitle, a.profession, a.professionalLife, a.education, a.school, a.unionId, a.password, a.sex, a.birthday, a.departmentId ,b.name departmentName from SysUser a left join SysDepartment b on a.departmentId=b.id where 1=1
and a.code=#{code}
and a.type=#{type}
and a.name like CONCAT('%',#{name},'%')
and a.company like CONCAT('%',#{company},'%')
and a.state=#{state}

select count(1)  from SysUser a where 1=1

	and a.code=#{code}


	and a.name=#{name}


	and a.state=#{state}

select a.*,b.name departmentName from SysUser a,SysDepartment b  where a.departmentId=b.id  and a.state=0 and a.type=1

	and a.departmentId =#{departmentId}


	and a.name like #{userName}

order by  CAST(a.code as SIGNED)
